// Utilizamos una función autoejecutable para que el código se ejecute automáticamente
(function() {
    // Definimos la función para inicializar el widget
    function initWidget() {
        // Creamos el contenedor del widget si no existe
        var container = document.getElementById("widget-container");
        if (!container) {
            container = document.createElement("div");
            container.id = "widget-container";
            document.body.appendChild(container);
        }

        // Crea el contenido del widget
        var widgetContent = document.createElement("div");
        widgetContent.innerHTML = "<button onclick='alert(\"Hola desde el widget!\")'>Haz clic</button>";

        // Agrega el contenido del widget al contenedor
        container.appendChild(widgetContent);
    }

    // Llamamos a la función para inicializar el widget
    initWidget();

    // Creamos el botón flotante sticky
    var floatingButton = document.createElement("button");
    floatingButton.textContent = "+";
    floatingButton.onclick = function() {
        alert("Hola desde el botón flotante!");
    };
    floatingButton.style.position = "fixed";
    floatingButton.style.bottom = "20px";
    floatingButton.style.right = "20px";
    floatingButton.style.zIndex = "1000";
    floatingButton.style.backgroundColor = "#007bff";
    floatingButton.style.color = "#fff";
    floatingButton.style.border = "none";
    floatingButton.style.borderRadius = "50%";
    floatingButton.style.width = "50px";
    floatingButton.style.height = "50px";
    floatingButton.style.fontSize = "20px";
    floatingButton.style.cursor = "pointer";

    // Estilos para el botón flotante al hacer hover
    floatingButton.addEventListener("mouseover", function() {
        floatingButton.style.backgroundColor = "#0056b3";
    });
    floatingButton.addEventListener("mouseout", function() {
        floatingButton.style.backgroundColor = "#007bff";
    });

    document.body.appendChild(floatingButton);
})();
